mapper.select:
select * from t_fpj where yxbz = '1'

	and djh = #{djh}


	and unionid = #{unionid}

limit 1

SELECT a.djh, a.skpid, a.gfmc, a.jylssj, a.gsdm, a.jshj, CASE isnull(e.ppdm) WHEN 1 THEN CONCAT('Logo_', a.gsdm) ELSE CONCAT('Logo_', a.gsdm, '-', e.ppdm) END AS Logo, c.ztbz, c.ckbtgyy, DATE_FORMAT(f.kprq, '%Y-%m-%d') kprq FROM t_jyls a JOIN t_fpj b ON a.djh = b.djh LEFT JOIN t_ckhk c ON a.djh = c.djh JOIN t_skp d ON a.skpid = d.id LEFT JOIN t_pp e ON d.pid = e.id LEFT JOIN ( SELECT djh, max(kprq) kprq FROM t_kpls GROUP BY djh HAVING sum( CASE WHEN pdfurl IS NOT NULL AND fpztdm = '00' THEN 1 ELSE 0 END ) = count(1) ) f ON f.djh = a.djh WHERE a.yxbz = '1' AND b.yxbz = '1' AND a.clztdm = '91'
and f.kprq >= #{qsrq}
and f.kprq < #{zzrq}
and a.jshj = #{je}
and b.unionid = #{unionid}
ORDER BY f.kprq DESC

select a.djh, a.gfmc, a.jylssj, a.jshj, c.ztbz, c.ckbtgyy from t_jyls a right join t_fpj b on a.djh=b.djh left join t_ckhk c ON a.djh=c.djh where a.yxbz='1' and b.yxbz='1' and a.clztdm='91'
and b.unionid = #{unionid}
order by b.id desc
