mapper.select:
select * from t_fpkc where yxbz = '1'

   and gsdm = #{gsdm}


   and xfid = #{xfid}


   and skpid = #{skpid}


   and fpzldm = #{fpzldm}


   and fpdm = #{fpdm}


   and fphms = #{fphms}


    order by #{orderBy}

limit 1

select fphms,fphmz from t_fpkc where yxbz = '1'

    and skpid = #{skpid}


    and fpdm = #{fpdm}


    and fpzldm = #{fpzldm}

select fphms,fphmz from t_fpkc where yxbz = '1'

    and skpid = #{skpid}


    and fpdm = #{fpdm}


    and fpzldm = #{fpzldm}

and id !=#{id}

select count(fphm) kpl from t_kpls where 1=1

   and gsdm = #{gsdm}


    and fpzldm = #{fpzldm}


and fpdm=#{fpdm}


and fphm between #{fphms} and #{fphmz}

SELECT a.id, a.xfid, a.skpid, a.fpzldm, a.fpdm, a.fphms, a.fphmz, b.xfsh, b.xfmc, c.kpdmc, e.fpzlmc, ( CONVERT (a.fphmz, SIGNED) - CONVERT (a.fphms, SIGNED) + 1 ) AS fpkcl, a.fpkcl kyl FROM t_fpkc a, t_xf b, t_skp c, t_fpzl e WHERE a.xfid = b.id AND a.skpid = c.id AND a.fpzldm = e.fpzldm AND a.yxbz = '1'
and a.gsdm = #{params.gsdm}
and a.xfid in #{params.xfid[${index}]}
and a.skpid in #{params.skpid[${index}]}
and a.xfid = #{params.xfids}
and a.skpid = #{params.skpids}
and a.fpdm = #{params.fpdm}
and a.fpzldm = #{params.fplx}
order by a.lrsj desc

select id skpid,kpdmc from t_skp where yxbz='1'

    and xfid = #{xfid}


    and gsdm = #{gsdm}


     and id in  #{skpid[${index}]}

select sum(a.fpkcl) kyl, b.xfmc,c.kpdmc,d.fpzlmc
  from t_fpkc a,t_xf b,t_skp c,t_fpzl d
  where a.yxbz='1' 
  and a.xfid = b.id
  and a.skpid = c.id
  and a.fpzldm = d.fpzldm

    and a.gsdm = #{gsdm}


    and a.xfid = #{xfid}


    and a.skpid = #{skpid}


   and a.fpzldm = #{fpzldm}

          select * from (select b.xfmc,b.xfsh,c.kpdmc,d.fpzlmc,sum(a.fpkcl) as kyl 
          from t_fpkc a,t_xf b,t_skp c,t_fpzl d
                 where a.xfid=b.id and a.skpid=c.id and a.fpzldm=d.fpzldm                 

			and a.gsdm = #{params.gsdm}



				#{params.xfs[${index}].id}

			or a.xfid is null)



				#{params.skps[${index}].id}

			or a.skpid is null)


		   and a.xfid = #{params.xfid}


		   and a.skpid = #{params.skpid}


		   and a.fpzldm = #{params.fplx}


		    and b.xfsh like CONCAT('%',#{params.xfsh},'%')

		group by a.xfid,a.skpid,a.fpzldm) t where 1=1

		    and t.kyl <= #{params.fpsl}
