SELECT * FROM ( SELECT xfmc, fpzl, sl * 100 sl, CASE jzjtbz WHEN 1 THEN '即征即退' ELSE '普通' END jzjtbz, sum(zsje) zsje, sum(fsje) fsje, sum(hjje) hjje, sum(zsse) zsse, sum(fsse) fsse, sum(hjse) hjse FROM V_FSLMX WHERE 1=1
