mapper.select:
select * from kplsvo where yxbz = '1'

    order by #{orderBy}

limit 1

select * from kplsvo where  fpzldm = '12' and fphm is not null and fphm != ''

    and gsdm = #{gsdm}


    and kprq >= ${kprqq}


    and kprq < date_add(str_to_date(#{kprqz},'%Y-%m-%d'),INTERVAL 1 day)



    and fpczlxdm = '11'


    and (fpczlxdm = '12' or fpczlxdm = '13')


    and kprq like concat('%',#{kprq},'%')


    and fpczlxdm = #{fpczlxdm}

select a.*,b.fpzlmc from kplsvo a,t_fpzl b where a.fpzldm = b.fpzldm and fpczlxdm = #{params.fpczlxdm} and fphm is not null

    and gsdm = #{params.gsdm}



        #{params.xfs[${index}].id} 

    or xfid is null)


    and ddh like concat('%',#{params.ddh},'%')


    and skpid =#{params.skpid}


    and xfid =#{params.xfid}


    and xfmc like concat('%',#{params.xfmc},'%')


    and gfmc like concat('%',#{params.gfmc},'%')


    and fpdm like concat('%',#{params.fpdm},'%')


    and fphm like concat('%',#{params.fphm},'%')


    and a.kprq >= #{params.kprqq}


    and a.kprq < date_add(str_to_date(#{params.kprqz},'%Y-%m-%d'),INTERVAL 1 day)


    and a.jylssj >= #{params.jyrqq}


    and a.jylssj < date_add(str_to_date(#{params.jyrqz},'%Y-%m-%d'),INTERVAL 1 day)


    and a.fpzldm=#{params.fpzl}

 order by kprq desc

SELECT * FROM ( SELECT fpzl, sl * 100 sl, CASE jzjtbz WHEN 1 THEN '即征即退' ELSE '普通' END jzjtbz, sum(zsje) zsje, sum(fsje) fsje, sum(hjje) hjje, sum(zsse) zsse, sum(fsse) fsse, sum(hjse) hjse FROM V_FP_FSLMX WHERE 1=1
and gsdm = #{gsdm}
and kprq >= #{kprqq}
and kprq < date_add(str_to_date(#{kprqz},'%Y-%m-%d'),INTERVAL 1 day)
GROUP BY fpzl, sl * 100, CASE jzjtbz WHEN 1 THEN '即征即退' ELSE '普通' END WITH ROLLUP ) t WHERE ( t.jzjtbz ='普通' OR t.jzjtbz = '即征即退' ) AND t.fpzl IS NOT NULL AND t.sl IS NOT NULL ORDER BY 1, 2, 3;

select sum(t.zsp) zspfs,sum(t.fsp) fspfs from (select case when fpczlxdm in ('11','21') then 1 else 0 end zsp, case when fpczlxdm in ('12','13') then 1 else 0 end fsp from t_kpls where gsdm=#{gsdm}
and kprq >= #{kprqq}
and kprq < date_add(str_to_date(#{kprqz},'%Y-%m-%d'),INTERVAL 1 day)
and fphm is not null) t

    select count(*) fpsl,t.kpny from 
(select substring(kprq,1,7) kpny,fpzldm from t_kpls
where yxbz='1' and fpztdm='00' and fphm is not null
and kprq is not null

   and gsdm = #{gsdm}


   and xfid = #{xfid}


   and skpid = #{skpid}


 ) t where 1=1

     and t.kpny >=#{kprqq} and t.kpny  <= #{kprqz}

 group by t.kpny

        select count(*) tqsl,tqny from 
(select DISTINCT substring(a.tqsj,1,7) tqny,a.djh,b.gsdm,b.xfid,b.fpzldm,b.skpid 
from t_tqjl a,t_jyls b
where a.djh= b.djh) t where 1=1

    and t.gsdm = #{gsdm}


    and t.xfid = #{xfid}


   and t.skpid = #{skpid}


   and t.tqny >=#{kprqq} and t.tqny  <= #{kprqz}

group by t.tqny
