mapper.select:
select * from t_qympk where yxbz = '1'

    order by ${orderBy}

limit 1

        select * from t_qympk where yxbz = '1'


			and gsdm = #{gsdm}


			and dwmc like CONCAT('%',#{dwmc},'%')


			and nsrsbh = #{nsrsbh}

        order by id

        select * from t_qympk where yxbz = '1'

			and dwmc like CONCAT('%',#{params.text},'%') or  nsrsbh = #{params.text}


			and gsdm = #{params.gsdm}


			and dwmc  like CONCAT('%',#{params.gfmc},'%')


			and nsrsbh = #{params.nsrsbh}


        order by nsrsbh
