mapper.select:
select * from roles where yxbz = '1'

   and name = #{name}


   and lrry in (select id from t_yh where gsdm = #{gsdm})


   and id != #{id}

limit 1

select * from roles where yxbz = '1' and lrry in (select id from t_yh where gsdm = #{gsdm})

    order by #{orderBy}


   and name = #{name}
